echo "Remote dump a copy of remote database $DATABASENAME with prefix $TABLE_PREFIX selected $TABLES_SELECTED to: $REMOTE_TEMP_DIR, extra options:  $NODATA $REMOTE_LOCKTABLES $REMOTE_DATABASES $REMOTE_DROPDB $REMOTE_CHARACTER $REMOTE_COMPA_M4 $REMOTE_COMPA_NT..."
ssh $SSH_USER@$IP "mysqldump -u $MYSQL_USER_REMOTE -p$MYSQL_PASS_REMOTE $NODATA $REMOTE_LOCKTABLES $REMOTE_DATABASES $REMOTE_DROPDB $REMOTE_CHARACTER $REMOTE_COMPA_M4 $REMOTE_COMPA_NT $DATABASENAME $TABLES_SELECTED_FOR_DUMP_LINE | gzip > $REMOTE_TEMP_DIR/$DATABASENAME$TABLES_SELECTED-remote.sql.gz"
